import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import axios from 'axios';

function SearchResults() {
  const [data, setData] = useState('react');
  const [query, setQuery] = useState('react');

  useEffect(() => {
    let ignore = false;

    async function fetchData() {
      // const result = await axios('https://hn.algolia.com/api/v1/search?query=' + query);

      const result = await axios('http://localhost:8083/getTA?query='+query);
      // const result = await axios('localhost:8083/getTA/' + query);
      if (!ignore) setData(result.data);
    }

    fetchData();
    return () => { ignore = true; }
  }, [query]);

  return (
    <>
      <input name="url_name" value={query} onChange={e => setQuery(e.target.value)} />
      <input type="button" value="Quick search !" onClick={e => setQuery(e.target.value)} />
      <div>
          <Text>
            Number of comments : {data.Nb_comments}
          </Text>
          <Text>
            Rating : {data.Rating}
          </Text>
      </div>
    </>
  );
}

// const rootElement = document.getElementById("root");
// ReactDOM.render(<SearchResults />, rootElement);
export default SearchResults;
